-- Make the database fusion_flirt
CREATE DATABASE IF NOT EXISTS fusion_flirt_db;
-- Use this database
USE fusion_flirt_db;
-- Add the surveys table to contain the survey topics currently available
CREATE TABLE IF NOT EXISTS  survey_tb(survey_ID INTEGER AUTO_INCREMENT PRIMARY KEY, surveyTopic TINYTEXT);
-- Create the table to contain all the questions
CREATE TABLE IF NOT EXISTS  questions_tb(question_ID INTEGER AUTO_INCREMENT PRIMARY KEY, questionNumber INTEGER, questionText TEXT, fk_survey_ID INTEGER, FOREIGN KEY (fk_survey_ID) REFERENCES survey_tb(survey_ID));
-- Create the table for the answers to be stored for each question
CREATE TABLE IF NOT EXISTS  answers_tb(answer_ID INTEGER AUTO_INCREMENT PRIMARY KEY, answer TEXT, questionAnswerLetter VARCHAR(5), fk_question_ID INTEGER, responseType TINYTEXT, FOREIGN KEY (fk_question_ID) REFERENCES questions_tb(question_ID));
-- Create a table to store the contact details about each user
CREATE TABLE IF NOT EXISTS  contact_details_tb(contact_ID INTEGER AUTO_INCREMENT PRIMARY KEY, phoneNumber VARCHAR(14), emailAddress TINYTEXT);
-- Create a table for storing each user's biography
CREATE TABLE IF NOT EXISTS  biography_tb(bio_ID INTEGER AUTO_INCREMENT PRIMARY KEY, bio TEXT);
-- Create a table to store the profile for each user, linking the profile ID to the biography ID
CREATE TABLE IF NOT EXISTS  profile_tb(profile_ID INTEGER AUTO_INCREMENT PRIMARY KEY, fk_bio_ID INTEGER, FOREIGN KEY (fk_bio_ID) REFERENCES biography_tb(bio_ID));
-- Create a table for addresses to store the address(es) of each user
CREATE TABLE IF NOT EXISTS  addresses_tb(address_ID INTEGER AUTO_INCREMENT PRIMARY KEY, town TEXT, postcode TINYTEXT, streetName TINYTEXT, houseName TINYTEXT);
-- Create a table to store the users account details
CREATE TABLE IF NOT EXISTS  users_tb(user_ID INTEGER AUTO_INCREMENT PRIMARY KEY, username TINYTEXT, firstname TINYTEXT, hashedPassword VARCHAR(255), surname TINYTEXT, dateOfBirth DATETIME, fk_address_ID INTEGER, fk_contact_ID INTEGER, fk_profile_ID INTEGER, FOREIGN KEY (fk_address_ID) REFERENCES addresses_tb(address_ID), FOREIGN KEY (fk_contact_ID) REFERENCES contact_details_tb(contact_ID), FOREIGN KEY (fk_profile_ID) REFERENCES profile_tb(profile_ID));
-- Create a table for storing the admins, including their clearance level
CREATE TABLE IF NOT EXISTS  admins_tb(fk_user_ID INTEGER PRIMARY KEY, clearanceLevel INTEGER, FOREIGN KEY (fk_user_ID) REFERENCES users_tb(user_ID));
-- Create a table for storing the user's photos 
CREATE TABLE IF NOT EXISTS  photos_tb(image_ID INTEGER AUTO_INCREMENT PRIMARY KEY, image BLOB);
-- Create a table to link the user's photos to their profile
CREATE TABLE IF NOT EXISTS  images_profile_link_tb(fk_image_ID INTEGER PRIMARY KEY, fk_profile_ID INTEGER, FOREIGN KEY (fk_image_ID) REFERENCES photos_tb(image_ID), FOREIGN KEY (fk_profile_ID) REFERENCES profile_tb(profile_ID));
-- Create a table to store all the suitable matches found in the database
CREATE TABLE IF NOT EXISTS  matches_tb(match_ID INTEGER AUTO_INCREMENT PRIMARY KEY, fk_user1_ID INTEGER, fk_user2_ID INTEGER, user1Responded BOOLEAN, user2Responded BOOLEAN, FOREIGN KEY (fk_user1_ID) REFERENCES users_tb(user_ID), FOREIGN KEY (fk_user2_ID) REFERENCES users_tb(user_ID));
-- Create a table to link the user's social media pages to their profiles
CREATE TABLE IF NOT EXISTS  social_media_links_tb(social_media_link_ID INTEGER AUTO_INCREMENT PRIMARY KEY, fk_profile_ID INTEGER, socialMediaLink TEXT, FOREIGN KEY (fk_profile_ID) REFERENCES profile_tb(profile_ID));
-- Create a table to authenticate each users new accounts
CREATE TABLE IF NOT EXISTS authentication_tb(fk_user_ID INTEGER PRIMARY KEY, activation_code VARCHAR(255), FOREIGN KEY (fk_user_ID) REFERENCES users_tb(user_ID));
-- For user's responses
-- Create a table to store the user's response to each question
CREATE TABLE IF NOT EXISTS users_response_tb(response_ID INTEGER AUTO_INCREMENT PRIMARY KEY, fk_user_ID INTEGER, fk_answer_ID INTEGER, users_response TEXT, FOREIGN KEY (fk_user_ID) REFERENCES users_tb(user_ID), FOREIGN KEY (fk_answer_ID) REFERENCES answers_tb(answer_ID));
-- Create a table to store the user's requirements for any question in the format of a range
CREATE TABLE IF NOT EXISTS users_wants_ranges_tb(response_ID INTEGER AUTO_INCREMENT PRIMARY KEY, fk_user_ID INTEGER, fk_answer_ID INTEGER, upper_bound INTEGER, lower_bound INTEGER, median_bound INTEGER, FOREIGN KEY (fk_user_ID) REFERENCES users_tb(user_ID), FOREIGN KEY (fk_answer_ID) REFERENCES answers_tb(answer_ID));
-- Create a table to store the user's requirements for any question in the format of a rating from 1-7
CREATE TABLE IF NOT EXISTS users_wants_radio_tb(response_ID INTEGER AUTO_INCREMENT PRIMARY KEY, fk_user_ID INTEGER, fk_answer_ID INTEGER, answer_rating INTEGER, FOREIGN KEY (fk_user_ID) REFERENCES users_tb(user_ID), FOREIGN KEY (fk_answer_ID) REFERENCES answers_tb(answer_ID));
